import React, {Component} from 'react';
import NavBar from './components/navbar';
import './App.css';
import Counters from './components/counters';

class App extends Component {
  state = {
    counters:[
        {id:1, value:232},
        {id:2, value:33},
        {id:3, value:1},
        {id:4, value:0}
    ]
}
testValue="dfsfsdfsf";

constructor(props) {
  super(props);
  this.state = {
    items:[],
    isLoaded:false,
  }
  /*
  super();
  console.log('App - Constructor');
  //this.state = this.props.something;
*/
}

componentDidMount(){
   //fetch('https://crm.zoho.com/crm/private/json/SalesOrders/getRecords?t&scope=crmapi&authtoken=bde93b7b47b5386350482676fc232c93&id=2598070000030053177')
  //fetch('http://jsonplaceholder.typicode.com/users')
  fetch('http://localhost:8000/api/movies')
  .then(res => res.json())
  .then(json => {
    this.setState({
      isLoaded:true,
      items:json,
    })
  });

}
onReset(){
  var e = document.getElementById('genre');
  var box = document.getElementById('displayBox');
  box.innerHTML ={testValue};
  var value = e.options[e.selectedIndex].value;
  console.log(value);
}

handleIncrement = counter => {
  console.log("increment");
    const counters = [...this.state.counters];
    const index = counters.indexOf(counter);
    counters[index] = {...counter};
    counters[index].value++;
    this.setState({counters});
};

handleReset = () => {
  const counters = this.state.counters.map(c=> {
        c.value = 0;
        return c;
    });
    this.setState({counters});
};

handleDelete = (counterId) => {
    const counters = this.state.counters.filter(c => c.id !== counterId);
    this.setState({counters});
};

  render(){
    var {isLoaded, items}=this.state;
    console.log("App - Rendered");
    if(!isLoaded){
      return <div>Loading...</div>;
    }else{
    return (

      <div className="App">
       <br></br> <div className="container">
        <h1>Movies App</h1>
        <div className="input-group">
        <select className="custom-select d-block w-1" id="genre" required>
                  <option value="">Choose...</option>
                  <option>Drama</option>
                  <option>Horror</option>
                  <option>SciFi</option>
                  <option>Documentary</option>
                </select>
              <div className="input-group-append">
                <button type="submit" className="btn btn-secondary" onClick={this.onReset} value="Drama">Submit</button>
              </div>
            </div>
            <br></br><div className="row" id="displayBox">Message Box
            <ul>
              {items.map(item =>(
              <li key={item.id}>
                Name: {item.id} | Email: {item.body}
              </li>
              ))};
            </ul>
          </div>
        </div>    
      </div>

/*
return(
<React.Fragment>
      <NavBar totalCounters={this.state.counters.filter(c =>c.value >0).length}/>
      <main className="container">
        <Counters 
        counters={this.state.counters}
        onReset={this.handleReset}
        onIncrement={this.handleIncrement}
        onDelete={this.handleDelete}
        />
      </main>
  </React.Fragment>);
  */
    );
    }
  }
}


export default App;
