import React, {Component} from 'react';
import './App.css';
import MovieDesc from './components/movieDesc';
class App extends Component {  
  constructor(props) {
    super(props);
    this.state = {
      items:[],
      isLoaded:false,
      currentIndex:1
    }
  }

  componentDidMount(){ 
    fetch('http://localhost:8000/api/movies')
   //fetch('http://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded:true,
        items:json,
      })
    }); 
  }

  handleIncrement = items => {
    /*
    var genreSelect = document.getElementById('genre');
    var currentGenre = genreSelect.options[genreSelect.selectedIndex].value;
    var box = document.getElementById('displayBox');
    var string = "";

    for(var i=0;i<items.length;i++){
      if(currentGenre==items[i].Genre){
      //  string += "<h2>"+items[i].title+"</h2>"+ items[i].body+"<img class='rounded float-left' width='100px' src='"+ items[i].imageURL+"'></img>";
      }
    }
   // this.state.currentIndex = 3;
    console.log(this.state.currentIndex);
    //box.innerHTML =string;
    */

  };

  setMovie = () =>{
    this.setState({currentIndex:this.state.currentIndex +1});
  };

  render(){
    var {isLoaded, items}=this.state;
    if(!isLoaded){
      return <div>Loading...</div>;
    }else{
    return ( 
      <div className="App">
        
       <br></br> <div className="container">
        <h1>API Integration</h1>
        <div className="input-group">
        <select className="custom-select d-block w-1" id="genre" required>
                  <option value="">Choose...</option>
                  <option>Drama</option>
                  <option>Horror</option>
                  <option>SciFi</option>
                  <option>Documentary</option>
                </select>
              <div className="input-group-append">
                <button onClick={this.setMovie}className="btn btn-secondary" >Set Movie</button>
               
              </div>
            </div>
            <br></br><div className="row" id="displayBox">
            <img src='{this.state.items[this.state.currentIndex].imageURL}'></img>
            {this.state.items[this.state.currentIndex].title}
            {this.state.items[this.state.currentIndex].Genre}
            {this.state.items[this.state.currentIndex].body}
            
           
          </div>
        </div>    
      </div>
    );
   }
  }
}
/*
 <MovieDesc key={0} value={this.state.items[this.state.currentIndex].id} /> 
 <button type="submit" className="btn btn-secondary" onClick={() => this.handleIncrement(items)} value="Drama">Submit</button>
{this.state.items.map(movieDesc =>(
              <MovieDesc key={0} value={movieDesc.email} /> 
            ))}
*/
export default App;
