import React, {Component} from 'react';
import './App.css';

class App extends Component {

constructor(props) {
  super(props);
  this.state = {
    items:[],
    isLoaded:false,
  }
}

componentDidMount(){ 
  fetch('http://jsonplaceholder.typicode.com/users')
  //fetch('http://localhost:8000/api/movies')
  .then(res => res.json())
  .then(json => {
    this.setState({
      isLoaded:true,
      items:json,
    })
  }); 
}

handleIncrement = counter => {
  var e = document.getElementById('genre');
  var value = e.options[e.selectedIndex].value;
  var {items}=this.state;
  fetch('http://jsonplaceholder.typicode.com/users')
  .then(res => res.json())
  .then(json => {
    this.setState({
      isLoaded:true,
      items:json,
    })
  });

  var box = document.getElementById('displayBox');
  if(value=="Drama"){
    for(i=0;i<3;i++){
      box.innerHTML =items[0].name;
    }

  }
  if(value=="Horror"){
    box.innerHTML =items[1].name;
  }
  if(value=="SciFI"){
    box.innerHTML =items[2].name;
  }
};

  render(){
    var {isLoaded, items}=this.state;
    console.log("App - Rendered");
    if(!isLoaded){
      return <div>Loading...</div>;
    }else{
    return ( 
      <div className="App">
       <br></br> <div className="container">
        <h1>API Integration</h1>
        <div className="input-group">
        <select className="custom-select d-block w-1" id="genre" required>
                  <option value="">Choose...</option>
                  <option>Drama</option>
                  <option>Horror</option>
                  <option>SciFi</option>
                  <option>Documentary</option>
                </select>
              <div className="input-group-append">
                <button type="submit" className="btn btn-secondary" onClick={() => this.handleIncrement(12)} value="Drama">Submit</button>
              </div>
            </div>
            <br></br><div className="row" id="displayBox">Message Box
            <ul>
              {items.map(item =>(
              <li key={item.id}>
                Name: {item.id} | Email: {item.email}
              </li>
              ))};
              </ul>
          </div>
        </div>    
      </div>
    );
   }
  }
}
export default App;
